mapper.select:
SELECT Nno, Nname, Nsex, Nage, Npwd
FROM Nurse
WHERE Nno = #{no} AND Npwd = #{pwd};

SELECT COUNT(*)
FROM Prescription p
WHERE p.Pstate = 0;

SELECT COUNT(*)
FROM Prescription p
WHERE p.Pstate = 1;

SELECT COUNT(*)
FROM Prescription p
WHERE p.Pstate = 1 AND p.Nno = #{Nno};

SELECT d.PDno, d.PDname, d.PDlife, i.PDbatch, i.PDnum, i.Sno, i.SAno, i.Stime
FROM Drug d, InventoryDrug i
WHERE d.PDno = i.PDno AND d.PDno = #{PDno};

SELECT p.Pno, p.Pid, p.Dno, p.Ptime, p.Nno, p.Htime, p.Pstate
FROM Prescription p
WHERE p.Pstate = 0;

SELECT p.Pno, p.Pid, p.Dno, p.Ptime, p.Nno, p.Htime, p.Pstate
FROM Prescription p
WHERE p.Pstate = 1;

SELECT p.Pno, p.Pid, p.Dno, p.Ptime, p.Nno, p.Htime, p.Pstate
FROM Prescription p
WHERE p.Pno = #{Pno};

SELECT PID.PDno, PID.PDnum, d.PDname
FROM Prescription p, PID, Drug d
WHERE p.Pno = PID.Pno AND PID.PDno = d.PDno AND p.Pno = #{Pno};

mapper.update:
UPDATE Nurse
SET Npwd = #{arg0}
WHERE Nno = #{arg1};
